# Relative path to `libfplll`
const libfplll_splitpath = ["lib", "libfplll.6.dylib"]

# This will be filled out by __init__() for all products, as it must be done at runtime
libfplll_path = ""

# libfplll-specific global declaration
# This will be filled out by __init__()
libfplll_handle = C_NULL

# This must be `const` so that we can use it with `ccall()`
const libfplll = "@rpath/libfplll.6.dylib"


"""
Open all libraries
"""
function __init__()
    global artifact_dir = abspath(artifact"fplll")

    # Initialize PATH and LIBPATH environment variable listings
    global PATH_list, LIBPATH_list
    # We first need to add to LIBPATH_list the libraries provided by Julia
    append!(LIBPATH_list, [joinpath(Sys.BINDIR, Base.LIBDIR, "julia"), joinpath(Sys.BINDIR, Base.LIBDIR)])
    # From the list of our dependencies, generate a tuple of all the PATH and LIBPATH lists,
    # then append them to our own.
    foreach(p -> append!(PATH_list, p), (CompilerSupportLibraries_jll.PATH_list, GMP_jll.PATH_list, MPFR_jll.PATH_list,))
    foreach(p -> append!(LIBPATH_list, p), (CompilerSupportLibraries_jll.LIBPATH_list, GMP_jll.LIBPATH_list, MPFR_jll.LIBPATH_list,))

    global fplll_path = normpath(joinpath(artifact_dir, fplll_splitpath...))

    push!(PATH_list, dirname(fplll_path))
    global latticegen_path = normpath(joinpath(artifact_dir, latticegen_splitpath...))

    push!(PATH_list, dirname(latticegen_path))
    global latsieve_path = normpath(joinpath(artifact_dir, latsieve_splitpath...))

    push!(PATH_list, dirname(latsieve_path))
    global libfplll_path = normpath(joinpath(artifact_dir, libfplll_splitpath...))

    # Manually `dlopen()` this right now so that future invocations
    # of `ccall` with its `SONAME` will find this path immediately.
    global libfplll_handle = dlopen(libfplll_path)
    push!(LIBPATH_list, dirname(libfplll_path))

    # Filter out duplicate and empty entries in our PATH and LIBPATH entries
    filter!(!isempty, unique!(PATH_list))
    filter!(!isempty, unique!(LIBPATH_list))
    global PATH = join(PATH_list, ':')
    global LIBPATH = join(LIBPATH_list, ':')

    # Add each element of LIBPATH to our DL_LOAD_PATH (necessary on platforms
    # that don't honor our "already opened" trick)
    #for lp in LIBPATH_list
    #    push!(DL_LOAD_PATH, lp)
    #end
end  # __init__()

